const fs = require('fs');

module.exports.writeTo = function(path, json, cb) {
    fs.writeFile(path,
        JSON.stringify(json), function(err) {
        if (err) cb(err);
        cb(null);
    });
};

module.exports.read = function(path, cb) {
    fs.readFile(path, function(err, data) {
        if (err) cb(err);
        cb(JSON.parse(data));
    })
};
